<!--home page displaying the inventory--><!DOCTYPE html><html lang="en">	<body>		<hr/>			<h1>Inventory</h1>		<hr/>				<?php						//connection information			require_once ("database/database_customer.php");						//check if the connection is successful			$conn = @mysqli_connect(					$host,					$user,					$pwd,					$sql_db			);									if (!$conn){				echo"<p>Connection error</p>";			}			else{								//select table				$sql_table = "customer";								//set sql command to add data to the table				$query = "select CustomerID, CustomerFname, CustomerLname, CustomerDOB, CustomerAddress, CustomerTele, CustomerSex FROM customer";											$result = mysqli_query($conn, $query);								//check if execution was successfull				if (!$result){					echo"<p> Something is wrong with ",  $query,"</p>";				}else{					//display retrivefd record					echo"<table border='1'>";					echo"<tr>"						."<th scope=\"col\">Customer ID</th>"						."<th scope=\"col\">Customer Fname</th>"						."<th scope=\"col\">Customer Lname</th>"						."<th scope=\"col\">Customer DOB</th>"						."<th scope=\"col\">Customer Address</th>"						."<th scope=\"col\">Customer Tele</th>"						."<th scope=\"col\">Customer Sex</th>"						."</tr>";												//loop through the database/database_inventory						while ($row = mysqli_fetch_assoc($result))						{							echo"<tr>";							echo"<td>", $row["CustomerID"],"</td>";							echo"<td>", $row["CustomerFname"],"</td>";							echo"<td>", $row["CustomerLname"],"</td>";							echo"<td>", $row["CustomerDOB"],"</td>";							echo"<td>", $row["CustomerAddress"],"</td>";							echo"<td>", $row["CustomerTele"],"</td>";							echo"<td>", $row["CustomerSex"],"</td>";							echo"</tr>";						}						echo"</table>";												mysqli_free_result($result);				}					mysqli_close($conn);			}		?>					<p><a href="update_customer.php">Add</a></p>			<p><a href="display_customer.php">View</a></p>	</body></html>