<!--this page is used to insert the data to the sales table--><!DOCTYPE html><html lang="en">	<body>		<h1>Update confirmation</h1>		<hr/>				<?php			$itemID = trim ($_POST["iID"]);			$SalesID = trim($_POST["SID"]);			$itemName = trim ($_POST["name"]);			$itemCat = trim ($_POST["category"]);			$itemQuantity = trim ($_POST["quantity"]);			$itemPrice = trim($_POST["Price"]) ;														//connection information			require_once ("database_inventory.php");						//check if the connection is successful			$conn = @mysqli_connect(					$host,					$user,					$pwd,					$sql_db			);						if (!$conn){				echo"<p>Connection error</p>";			}else{								//select table				$sql_table = "sales";								//set sql command to add data to the table				$query = "insert into $sql_table (itemID, SalesID, itemName, itemCategory, itemQuantity, itemPrice)							values ('$itemID', '$SalesID', '$itemName', '$itemCat', '$itemQuantity', '$itemPrice')";											$result = mysqli_query($conn, $query);				$result = $result + 1;				//check if execution was successfull				if (!$result){					echo"<p class=\"wrong\">something is wrong with", $query,"</p>";				}else{					echo "<p class=\"OK\">Successfully added new Item</p>";				}					mysqli_close($conn);			}			?>						<p><a href="update_sales.php">Add</a></p>			<p><a href="display_sales.php">View</a></p>	</body></html>